import React from 'react'
import { Layout, Table, Row, Col, DatePicker, TimePicker, Spin } from 'antd'
import { Line, Bar } from 'react-chartjs-2'
import { CSVLink } from 'react-csv'

import { constants, columns, csvHeaders } from './constants'
import {
  getURL, getSource, fmtFloat,
  lineChartOptions, lineChartWithPriceVolume, lineChartWithCloseVolume,
  barChartOptions, barChartWithVolumes,
  getMarketDataCSV, getOhlcvCSV, getBalanceCSV } from './util'

// Styles.
import './App.css'
import { CloudDownload } from '@material-ui/icons'
import * as data from './dummy.json';
const keyMerged = 'Merged'
// const mockData  = require('./dummy.json');

const word = data['default']['data'];

// var data = require('json!./dummy.json');
class App extends React.Component {
  data = {
    origin: [],
    balance: [],
    trade: [],
    chart: {},
    csv: {
      market: {},
      ohlcv: {},
      balance: {}
    },
    searchDate: {},
    searchTime: {}
  };

  state = {
    ready: false,
    readyBalanceCSV: false,
    tokenMetric: [],
    marketData: [],
    ohlcvData: [],
    balanceData: []
  };

  componentWillMount () {
    var asyncLoading = [this.loadReport(), this.loadBalance().then(() => this.loadTrade())]
    Promise.all(asyncLoading).then(() => this.setState({ ready: true }, () => this.calcVolume()))
  }

  getMarketDataRender () {
    return <div>
      {this.state.ready &&
      <div>
        <Row justify='center' type='flex'>
          <Col span={4}><h3>Market Data</h3></Col>
          <Col span={20}>
            <DatePicker format='YYYY/MM/DD' onChange={(d, ds) => this.onSearchByTime(constants.key.marketData, true, ds)} />
          </Col>
        </Row>
        <br />
        <Table
          size='small'
          style={{ minWidth: '1000px' }}
          pagination={false}
          rowKey={record => record.category}
          columns={columns.MarketData}
          dataSource={word}
        />
        {console.log(this.state.marketData)}
        {console.log("ye word hai",word)}


      </div>
      }
    </div>
  }



  render () {
    return (
      <Layout className='layout'>
        <Layout.Header>
          Header
        </Layout.Header>
        <Layout.Content style={{ padding: '5vh 5vw 0vh 5vw', backgroundColor: '#fff', minHeight: '70vh' }}>
          {this.state.ready
            ? <div>
              {this.getMarketDataRender()}
              <br />
            </div>
            : <center>
              <h1>Loading...</h1>
              <Spin size='large' />
            </center>
          }
        </Layout.Content>
        <Layout.Footer>
          <center>UI creator team TheBigShot</center>
        </Layout.Footer>
      </Layout>
    )
  }
}

export default App
